// A service to manage audio playback for the game.
// Sounds are embedded as Base64 data URIs to avoid external file dependencies.

const sounds = {
  // A positive, magical chime for a correct answer
  correct: 'data:audio/mpeg;base64,',
  // A soft, negative buzz for an incorrect answer
  incorrect: 'data:audio/mpeg;base64,',
  // A whoosh sound for making a choice
  choice: 'data:audio/mpeg;base64,',
  // An opening fanfare for starting the game
  start: 'data:audio/mpeg;base64,',
  // A concluding fanfare for game over
  gameOver: 'data:audio/mpeg;base64,',
};

/**
 * Plays a sound from a Base64 data URI.
 * @param soundSrc The Base64 encoded audio source.
 */
function playSound(soundSrc: string) {
  try {
    const audio = new Audio(soundSrc);
    // Set a lower volume to be less intrusive
    audio.volume = 0.5;
    audio.play().catch(e => {
        // Autoplay can be blocked by the browser, we'll log this but not crash.
        console.warn("Audio playback was prevented by the browser:", e)
    });
  } catch (e) {
    console.error("Could not play audio:", e);
  }
}

export const audioService = {
  playCorrectSound: () => playSound(sounds.correct),
  playIncorrectSound: () => playSound(sounds.incorrect),
  playChoiceSound: () => playSound(sounds.choice),
  playStartSound: () => playSound(sounds.start),
  playGameOverSound: () => playSound(sounds.gameOver),
};
